package com.everwing.coreservice.common.wy.entity.business.watermeter;/** * @describe 加载水电表和建筑信息的综合实体 * @author QHC * @date 2017-08-08 11:55:24 */public class TcMeterBuilding implements java.io.Serializable {			private static final long serialVersionUID = 6334031464487961631L;		/** 主键id **/	private String id;		/** 水表编号 **/	private String code;		/** 水电表名称 **/	private String waterMeterName;		private String buildingCode;	    /**  后面用户选择公式需要使用pid  **/	private String pid;		/** 建筑名称 **/	private String buildingName;		/** 项目id **/	private String projectId;	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public String getWaterMeterName() {		return waterMeterName;	}	public void setWaterMeterName(String waterMeterName) {		this.waterMeterName = waterMeterName;	}	public String getPid() {		return pid;	}	public void setPid(String pid) {		this.pid = pid;	}	public String getBuildingName() {		return buildingName;	}	public void setBuildingName(String buildingName) {		this.buildingName = buildingName;	}	public String getProjectId() {		return projectId;	}	public void setProjectId(String projectId) {		this.projectId = projectId;	}	public String getBuildingCode() {		return buildingCode;	}	public void setBuildingCode(String buildingCode) {		this.buildingCode = buildingCode;	}			}