package com.everwing.coreservice.common.wy.entity.configuration.project;/******************************************************************************* * javaBeans * 针对水电表的分摊---单个分摊关联的建筑信息 * <table explanation> * @author 2017-07-03 16:27:13 *  */	public class TBsShareBuildingRelation implements java.io.Serializable {		private static final long serialVersionUID = 8138294058101447826L;	/** 主键主键id **/	private String id;		/** 关联分摊任务id   **/	private String shareTaskId;		/** 关联建筑id  **/	private String relationBuildingCode;		/** 关联建筑全名称  **/	private String relationBuildingName;		/**关联建筑父ID**/	private String relationBuildingPid;	/**	 * 增加两个实际分摊中的字段：分摊金额，使用量（水电量）	 */		/**  分摊金额  **/	private Double shareMoney;		/** 使用量  **/	private Double useAmount;		public String getRelationBuildingPid() {		return relationBuildingPid;	}	public void setRelationBuildingPid(String relationBuildingPid) {		this.relationBuildingPid = relationBuildingPid;	}		public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getShareTaskId() {		return shareTaskId;	}	public void setShareTaskId(String shareTaskId) {		this.shareTaskId = shareTaskId;	}	public String getRelationBuildingCode() {		return relationBuildingCode;	}	public void setRelationBuildingCode(String relationBuildingCode) {		this.relationBuildingCode = relationBuildingCode;	}	public String getRelationBuildingName() {		return relationBuildingName;	}	public void setRelationBuildingName(String relationBuildingName) {		this.relationBuildingName = relationBuildingName;	}	public Double getShareMoney() {		return shareMoney;	}	public void setShareMoney(Double shareMoney) {		this.shareMoney = shareMoney;	}	public Double getUseAmount() {		return useAmount;	}	public void setUseAmount(Double useAmount) {		this.useAmount = useAmount;	}		}