mapper.select:
SELECT id, opr_id, opr_name, STATUS, IFNULL(cash_total,0.00) cashTotal, IFNULL(cash_gaven,0.00) cashGaven, IFNULL(cash_not_give,0.00) cashNotGive, IFNULL(wx_total,0.00) wxTotal, IFNULL(union_total,0.00) unionTotal, IFNULL(alipay_total,0.00) alipayTotal, IFNULL(bank_receipts_total,0.00) bankReceiptsTotal, receive_id, create_time, total_id, end_time,total_num FROM t_jg_total_calculation WHERE receive_id=#{receiveId} AND project_id=#{projectId}
AND opr_name LIKE CONCAT('%',#{oprName},'%')
AND status = #{status}
ORDER BY create_time DESC

mapper.update:
		UPDATE t_jg_total_calculation SET STATUS=#{status} ,end_time=NOW()  WHERE receive_id=#{receiveId} AND project_id=#{projectId} 
		AND status=1
		AND id IN 

  			#{id}

		UPDATE t_jg_total_calculation SET total_id = NULL 
		WHERE total_id IN

  			#{id}
