mapper.select:

select

  distinct

'true' as QUERYID,

from tc_order




  order by ${orderByClause}

    limit ${limitStart} , ${limitEnd}

select tco.order_code,tco.building_code,tco.order_type_id_1,tco.order_type_id_2,tco.order_type_id_3, tco.is_urgent,tco.need_callback,tco.source_type,tco.create_time,tco.update_time,tco.status,tco.description, tcb.building_full_name as buildingFullName ,tcb.project_id as projectId ,tbp.project_name as projectName, tpc.register_phone as customerMobile,tco.procesInstructions as procesInstructions,tsu.staff_name as principalSysUser from tc_order tco LEFT JOIN tc_building tcb on tco.building_code = tcb.building_code LEFT JOIN t_bc_project tbp on tcb.project_id = tbp.project_id Left JOIN tc_person_cust tpc on tpc.cust_id = tco.cust_id Left JOIN t_sys_user tsu on tsu.user_id = tco.principal_sys_user_id where principal_sys_user_id = #{accountId}
and description LIKE CONCAT('%',#{description},'%')
and date_format(tco.create_time,'%Y-%m') = #{date}
order by tco.create_time ;

mapper.update:
UPDATE tc_order set status = #{status}, update_time = #{processDate},
finish_time=now(),
update_by = #{updateBy}, procesInstructions=#{procesInstructions} where order_code = #{orderCode}
